let cartBtnElem = document.querySelector("#cartBtn"),
    cartBoxElem = document.querySelector(".cart-container");

function showCartBox() {
    overlayElem.classList.toggle("show"), cartBoxElem.classList.toggle("cart-show")
}

function closeCartBoxWithEsc(e) {
    "Escape" === e.key && (overlayElem.classList.remove("show"), cartBoxElem.classList.remove("cart-show"))
}
cartBtnElem.addEventListener("click", showCartBox), window.addEventListener("keyup", closeCartBoxWithEsc);
let preloadElem = document.querySelector(".preload");

function preloadHandler() {
    preloadElem.classList.add("hidden")
}
window.addEventListener("load", preloadHandler);
let showSearchBoxBtn = document.querySelector("#searchBtn"),
    closeSearchBoxBtn = document.querySelector("#closeSearchBoxBtn"),
    searchBox = document.querySelector(".search-box");

function showSearchBox() {
    searchBox.classList.toggle("show-search-box"), overlayElem.classList.toggle("show")
}

function closeSearchBox() {
    searchBox.classList.remove("show-search-box"), overlayElem.classList.remove("show")
}
var path = window.location.pathname,
    page = path.split("/").pop();
"index.html" === page || "" === page ? console.log("index.html") : (showSearchBoxBtn.addEventListener("click", showSearchBox), closeSearchBoxBtn.addEventListener("click", closeSearchBox));
let overlayElem = document.querySelector(".overlay");

function closeBoxes() {
    overlayElem.classList.remove("show"), cartBoxElem.classList.remove("cart-show"), searchBox.classList.remove("show-search-box"), navbarListElem.classList.remove("navbar-responsive", "fix")
}
overlayElem.addEventListener("click", closeBoxes);
let navbarElem = document.querySelector(".navbar"),
    toTopElem = (document.addEventListener("scroll", function() {
        100 < document.documentElement.scrollTop ? (navbarElem.classList.add("scroll"), searchBox.style.top = window.top) : navbarElem.classList.remove("scroll")
    }), document.querySelector("#toTop"));

function toTopHandler() {
    200 < window.scrollY ? toTopElem.classList.add("active") : toTopElem.classList.remove("active")
}
window.addEventListener("scroll", toTopHandler);
let showNavbarBtnElem = document.querySelector("#showNavbarBtn"),
    navbarListElem = document.querySelector(".mobile-navbar"),
    openSubMenuBtn = document.querySelectorAll(".show-mobile-menu");

function showNavbar() {
    navbarListElem.classList.contains("navbar-responsive") ? (navbarListElem.classList.remove("navbar-responsive", "fix"), overlayElem.classList.remove("show")) : (navbarListElem.classList.add("navbar-responsive", "fix"), overlayElem.classList.add("show"))
}
let i;
for (i = 0; i < openSubMenuBtn.length; i++) openSubMenuBtn[i].addEventListener("click", function() {
    this.classList.toggle("m-active");
    var e = this.nextElementSibling;
    e.classList.contains("show-sub-menu") ? e.classList.remove("show-sub-menu") : e.classList.add("show-sub-menu")
});
showNavbarBtnElem.addEventListener("click", showNavbar), $(document).ready(function() {
    $(".product-carousel").owlCarousel({
        loop: !0,
        margin: 15,
        nav: !0,
        rtl: !0,
        responsive: {
            0: {
                items: 1
            },
            600: {
                items: 2
            },
            1200: {
                items: 3
            }
        }
    })
}), $(".count").each(function() {
    $(this).prop("Counter", 0).animate({
        Counter: $(this).text()
    }, {
        duration: 4e3,
        easing: "swing",
        step: function(e) {
            $(this).text(Math.ceil(e))
        }
    })
});